import React, {Component} from 'react';
import FormInput from "../form-imput/form-input.component";
import CustomButton from "../custom-button/custom-button.component";
import {auth,createUserProfileDocument} from "../../firebase/firebase.utils";
import "./sign-up.styles.scss"

class SignUpComponent extends Component {
    constructor() {
        super();
        this.state = {
            displeyName:'',
            email:'',
            password:'',
            confirmPassword:''
        }
    }

    handleSubmit = async event=>{
        event.preventDefault();

        const {displayName, email, password, confirmPasswored} = this.state
        if(password!==confirmPasswored){
            alert("Password don't match")
            return 
        }
        try{
           const {user} =await auth.createUserWithEmailAndPassword(email,password)

           await createUserProfileDocument(user, {displayName})

            this.setState(
                {
                    displeyName:'',
                    email:'',
                    password:'',
                    confirmPassword:''
                })

        }catch (error) {
            console.error(error)
        }
    }

    handleChange = event=>{
        const {name, value}=event.target
        this.setState({
            [name]:value
        })
    }

    render() {
        const {displayName, email, password,confirmPassword}= this.state
        return (
            <div className='sign-in'>
                <h2 className='title'>I do not have a account</h2>
                <span>Sign up with your eamil and password</span>
                <form onSubmit={this.handleSubmit} className="sign-up">
                    <FormInput
                        type='text'
                        name='displayName'
                        value={displayName}
                        onChange={this.handleChange}
                        label='Display Name'
                        required
                    />

                    <FormInput
                        type='email'
                        name='email'
                        value={email}
                        onChange={this.handleChange}
                        label='Email'
                        required
                    />

                    <FormInput
                        type='password'
                        name='password'
                        value={password}
                        onChange={this.handleChange}
                        label='Password'
                        required
                    />

                    <FormInput
                        type='password'
                        name='confirmPassword'
                        value={confirmPassword}
                        onChange={this.handleChange}
                        label='Confirm password'
                        required
                   />
                    <CustomButton type='submit'>SIGN UP</CustomButton>
                </form>
            </div>
        );
    }
}

export default SignUpComponent;
