import React from "react";
import {connect} from 'react-redux'
//import './colection-item.style.scss'
import CustomButton from "../custom-button/custom-button.component";
import {addItem} from "../../redux/cart/cart.types";
import {CollectionFooterContainer, CollectionItemContainer, ImageContainer} from "./collection-item.styles";

const CollectionItem = ({item,addItem})=>{
    const { name, price, imageUrl}=item
    return( <CollectionItemContainer>
                <ImageContainer
                     style={{
                         backgroundImage:`url(${imageUrl})`
                     }}
                >

                </ImageContainer>
                <CollectionFooterContainer>
                    <span className='name'>{name}</span>
                    <span className='price'>{price}</span>
                </CollectionFooterContainer>
                <CustomButton inverted onClick={()=> addItem(item)} inverted>
                    Add to cart</CustomButton>

            </CollectionItemContainer>)
}




const mapDispatchToProps = dispatch=>({
    addItem:item=>dispatch(addItem(item))
})

export  default connect(null,mapDispatchToProps)(CollectionItem)
