import React,{useEffect} from "react";
import {connect} from 'react-redux'
import "./collection.component"
import {selectCollection} from "../../redux/shop/shop.selector";
import CollectionItem from "../../components/collection-item/collection-item.component";
import "./colection.style.scss"
import {firestore} from "../../firebase/firebase.utils";

const CollectionPage =({collection})=>{
    useEffect(()=>{
        console.log('I am sublscribing')
        const unsubscribeFromCollections = firestore.collection('collections').onSnapshot(async  snapshot=>console.log(snapshot))
        return ()=>{
            console.log("I am unsubscribing")
            unsubscribeFromCollections()
        }
    },[])
    const {title,items}=collection;
    return  <div className="collection-page">
                <h2 className='title'>{title}</h2>
                <div className="items">
                    {items.map(
                        item=><CollectionItem key={item.id} item={item}/>
                    )}
                </div>
            </div>

}



const mapStateToProps = (state,ownProps)=>({
    collection:selectCollection(ownProps.match.params.collectionId)(state)
})

export default connect(mapStateToProps)(CollectionPage)
