import React, {Component} from 'react';
import {connect} from 'react-redux'
import FormInput from "../form-imput/form-input.component";
import CustomButton from "../custom-button/custom-button.component";
import {signUpStart} from "../../redux/user/user.actions";
import "./sign-up.styles.scss"

class SignUpComponent extends Component {
    constructor() {
        super();
        this.state = {
            displeyName:'',
            email:'',
            password:'',
            confirmPassword:''
        }
    }

    handleSubmit = async event=>{
        event.preventDefault();

        const {signUpsStart}=this.props

        const {displayName, email, password, confirmPassword} = this.state
        if(password!== confirmPassword){
            alert("Password don't match")
            return 
        }
        signUpStart({displayName,email,password})
    }

    handleChange = event=>{
        const {name, value}=event.target
        this.setState({
            [name]:value
        })
    }

    render() {
        const {displayName, email, password,confirmPassword}= this.state
        return (
            <div className='sign-in'>
                <h2 className='title'>I do not have a account</h2>
                <span>Sign up with your eamil and password</span>
                <form onSubmit={this.handleSubmit} className="sign-up">
                    <FormInput
                        type='text'
                        name='displayName'
                        value={displayName || ""}
                        onChange={this.handleChange}
                        label='Display Name'
                        required
                    />

                    <FormInput
                        type='email'
                        name='email'
                        value={email}
                        onChange={this.handleChange}
                        label='Email'
                        required
                    />

                    <FormInput
                        type='password'
                        name='password'
                        value={password}
                        onChange={this.handleChange}
                        label='Password'
                        required
                    />

                    <FormInput
                        type='password'
                        name='confirmPassword'
                        value={confirmPassword}
                        onChange={this.handleChange}
                        label='Confirm password'
                        required
                   />
                    <CustomButton onClick={handleSubmit} type='submit'>SIGN UP</CustomButton>
                </form>
            </div>
        );
    }
}

const mapDispatchToProps =dispatch=>({
    signUpStart:userCredentials=>dispatch(signUpStart(userCredentials))
})

export default connect(null, mapDispatchToProps)(SignUpComponent);
